import {db} from "./firebase"; 
import firebase from "firebase";
// import { usePromise } from "./Presenter/usePromise";

function SaveToFirebase(list) { 
  var unsubscribe = firebase.auth().onAuthStateChanged((user)=>{
    
    console.log("save: ",  user.uid);
    if (user) {
      try {
        db.ref(`${user.uid}`).push(
          list 
        );
      } catch (error) {
        console.log(error);
      }
    }
  });

  unsubscribe();
}

function ReadFromFirebase({model}){
  
  firebase.auth().onAuthStateChanged((user)=>{
    // console.log(model.getUserID());
    // console.log("Read: ", user.uid);
    if (user) {
      try {
        db.ref(`${user.uid}`).on("value", (snapshot) =>{


          if(snapshot.val()){
            const values = Object.values(snapshot.val());
            const list = Object.entries(snapshot.val());

            const nameKeyMapping = list.map( recipe => {
              return {
                key: recipe[0], 
                name: recipe[1].name
              };
            });

            // console.log("Firebase Read Recipes: ", values);
            // console.log("Firebase Key Mapping: ", nameKeyMapping);

            model.setRecipeList(values);
            model.setRecipeKeys(nameKeyMapping);
            model.setUserID(user.uid);
          }
        });
      } catch (error) {
        console.log(error);
      }
    } else {
      // console.log("No user");
    }
  });

  // unsubscribe();
};

function RemoveFromDatabase(id){
  firebase.auth().onAuthStateChanged((user)=>{
    if (user) {
      try {

        db.ref(`${user.uid}/` + id).remove();

      } catch (error) {
        console.log(error);
      }
    }
  });
}

function UpdateFirebase(id, name, ingredientList){
  firebase.auth().onAuthStateChanged((user)=>{
    if (user) {
      try {
        db.ref(`${user.uid}/` + id)
          .update({
            'name': name,
            'ingredientList': ingredientList
          });
      } catch (error) {
        console.log(error);
      }
    }
  });
}



export {SaveToFirebase, ReadFromFirebase, RemoveFromDatabase, UpdateFirebase};
