import './App.css'; // add script like in the previous html file
import Auth from './Login/auth';

//Presenter
import SaveMolecules from './Presenter/savePage.js';
import StoredMolecules from './Presenter/storedPage.js';
import Search from './Presenter/search.js';
import Details from './Presenter/details';
import BarChart from './components/barCharts';

import Homepage from './View/homepage';

import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
import useModelProp from './Presenter/useModelProp';

// Firebase
import firebase from "firebase";

const App= ({model}) => {

  return <Router>
    <div >
        <div className = "flexContainer"> 
          <nav>
          {!useModelProp(model, "userID") ? 
          // <ul className = "nav">
          //   <li>
          //   <Link to="/">NuitionBuddy</Link>
          //   </li>
          //   <li><Link to="/login">Login</Link></li>
          // </ul>
          <p></p>
          :
          <ul className = "nav">
            <li>
            <Link to="/">
            <img id="title-icon" alt="favicon" src={process.env.PUBLIC_URL + "/images/icons8-omlette-64.png"} width="30px"></img>
              </Link>
            </li>
            {<li className="nav-title" ><Link to="/search" onClick={()=>{model.clearList("clear");}}>New Recipe</Link></li>}
            <li className="nav-title"><Link to="/stored">My Recipes</Link></li>
            <li className="nav-title" ><Link to="/" onClick={
              ()=> {
                firebase.auth().signOut(); 
                // history.push("/login"); 
                window.localStorage.clear();
                model.setUserID(undefined);
                model.emptyRecipeList();
                model.clearList("clear");
                // console.log(model.getingredientList());
                // console.log(model.getRecipeList());
              }
              
              }>Log out</Link></li>
            
          </ul>}
          </nav>

          {/* A <Switch> looks through its children <Route>s and
              renders the first one that matches the current URL. */}
          
            {/*Route = Show*/}

            {!useModelProp(model, "userID") ?
            <Switch>
              <Route path="/login">
                <Auth model={model}/>
              </Route>
              <Route path="/"> 
                <Homepage />
              </Route> 
            </Switch>
            : 
            <Switch>
              {/* <Route path="/home"> 
                <Homepage  />
              </Route> */}
              <Route path="/graph"> 
                <BarChart model ={model} />
              </Route>
              <Route path="/save">
                <SaveMolecules model={model}/>
              </Route>
              <Route path="/stored">
                <StoredMolecules model={model} />
              </Route>
              <Route path="/details">
                <Details model={model}/>
              </Route>
              {/* <Route path="/login">
                <Auth model={model}/>
              </Route> */}
              <Route path="/search">
              <Search model = {model}/>
              </Route>
              <Route path="/">
              <StoredMolecules model={model} />
              </Route>
            </Switch>}       
          </div>
        </div>
  </Router>
}
export default App;
